mapper.select:
SELECT
	DISTINCT(microCategory)
FROM
	k1_tb_item_category
WHERE 
	largeCategory = #{largeCategory} 
	AND middleCategory = #{middleCategory}
	AND smallCategory = #{smallCategory};

mapper.insert:
INSERT INTO k1_tb_item_info
	(itemCode
	,mainBusinessCode
	,itemName
	,itemType
	,categoryCode
	,itemStandard
	,itemOrigin
	,itemStatus
	,itemComment
	,regDate
	,updateDate
) VALUES(
	CONCAT('itemCode_',
	
		(SELECT
			CASE
				WHEN (MAX(RIGHT(itemCode,3))+1 < 10) THEN CONCAT('00',MAX(RIGHT(itemCode,3))+1)
				WHEN (MAX(RIGHT(itemCode,3))+1 < 100) THEN CONCAT('0',MAX(RIGHT(itemCode,3))+1)
			ELSE MAX(RIGHT(itemCode,3))+1
			END AS newCodeNum
		FROM
			k1_tb_item_info)
	
	)
	,#{mainBusinessCode}
	,#{itemName}
	,#{itemType}
	,CONCAT('categoryCode_',#{categoryCode})
	,#{itemStandard}
	,#{itemOrigin}
	,#{itemStatus}
	,#{itemComment}
	,NOW()
	,NOW()
);

mapper.select:
SELECT
	categoryCode
FROM
	k1_tb_item_category
WHERE
	largeCategory = #{largeCategory}
	
		AND middleCategory = #{middleCategory}
	
	
		AND smallCategory = #{smallCategory}
	
	
		AND microCategory = #{microCategory}
