mapper.select:
SELECT RIGHT(s.inventoryCode,3) AS inventoryCode ,s.mainBusinessCode ,RIGHT(s.itemCode,3) AS itemCode ,s.itemName ,s.itemType ,s.largeCategory ,s.middleCategory ,s.smallCategory ,s.itemStandard ,s.itemOrigin ,s.unitPrice AS productPrice ,s.itemCount AS totalCount ,s.totalPrice ,RIGHT(s.warehouseCode,3) AS warehouseCode ,s.warehouseName ,s.location ,s.outPlace ,s.totalWeight ,s.`status` ,s.`comment` ,s.regDate AS stockRegDate ,s.updateDate AS stockUpdateDate FROM k1_view_stock AS s WHERE s.mainBusinessCode = #{mainBusinessCode}
AND s.itemType REGEXP #{types}
AND s.warehouseCode REGEXP #{wList}
ORDER BY s.inventoryCode DESC;

mapper.update:
UPDATE k1_tb_stock AS s SET s.itemCode = CONCAT('itemCode_',#{itemCode})
,s.purchaseTsCode = CONCAT('purchase_ts_stCode_',#{purchaseTsCode})
,s.productPriceCode = CONCAT('productPriceCode_',#{productPriceCode})
,s.warehouseCode = CONCAT('warehouseCode_',#{warehouseCode}) ,s.itemCount = ${totalCount} ,s.totalWeight = ${totalWeight} ,s.`status` = #{status}
,s.`comment` = #{comment}
,s.updateDate = NOW() WHERE s.inventoryCode = CONCAT('inventoryCode_', #{inventoryCode})
