mapper.select:
SELECT RIGHT(a.stockAdjCode,3) AS stockAdjCode ,a.stockReasonCode ,a.reason AS stockReason ,a.clientCode ,a.businessName ,GROUP_CONCAT(DISTINCT ad.itemName SEPARATOR ', ') AS itemList ,a.totalPrice ,a.briefs ,a.requestDate ,a.adjDate ,a.endDate ,a.managerId ,a.managerName ,a.regDate ,LEFT(a.updateDate,10) AS updateDate FROM k1_view_stock_adjustment a JOIN (SELECT ad.stockAdjDetailCode ,ad.stockAdjCode ,i.itemName FROM k1_tb_stock_adjustment_detail ad JOIN k1_view_item_info i ON ad.itemCode = i.itemCode ) AS ad ON a.stockAdjCode = ad.stockAdjCode
a.mainBusinessCode = #{mainBusinessCode}
AND a.stockReasonCode = ${stockReasonCode}
OR a.sendWarehouse = CONCAT('warehouseCode_',#{warehouseCode})
OR a.receiveWarehouse = CONCAT('warehouseCode_',#{warehouseCode})
GROUP BY a.stockAdjCode;

SELECT RIGHT(a.stockAdjCode,3) AS stockAdjCode ,RIGHT(a.shipmentPlanCode,3) AS shipmentPlanCode ,RIGHT(a.returnRegCode,3) AS returnRegCode ,a.clientCode ,a.businessName ,RIGHT(a.contractCode,3) AS contractCode ,RIGHT(a.sendWarehouse,3) AS sendWarehouse ,w1.warehouseName AS sendWarehouseName ,w1.location AS sendWarehouseLocation ,w1.outPlace AS sendWarehouseOutPlace ,RIGHT(a.receiveWarehouse,3) AS receiveWarehouse ,w2.warehouseName AS receiveWarehouseName ,w2.location AS receiveWarehouseLocation ,w2.outPlace AS receiveWarehouseOutPlace ,a.totalPrice ,a.totalWeight ,RIGHT(a.materialOrderCode,3) AS materialOrderCode ,RIGHT(a.productOrderCode,3) AS productOrderCode ,a.deliveryCost ,RIGHT(a.deliveryCode,3) AS deliveryCode ,a.briefs ,a.managerId ,a.managerName ,a.sendPlanDate ,a.receivePlanDate ,a.requestDate ,a.adjDate ,a.endDate ,a.regDate ,a.updateDate FROM k1_view_stock_adjustment AS a LEFT JOIN k1_tb_warehouse_info AS w1 ON a.sendWarehouse = w1.warehouseCode LEFT JOIN k1_tb_warehouse_info AS w2 ON a.receiveWarehouse = w2.warehouseCode
a.stockAdjCode = CONCAT('stockAdjCode_',#{stockAdjCode})
AND a.mainBusinessCode = #{mainBusinessCode}
AND a.stockReasonCode = ${stockReasonCode}

mapper.insert:
INSERT INTO k1_tb_stock_adjustment
	(stockAdjCode, mainBusinessCode, stockReasonCode
	, clientCode
	, contractCode
	, materialOrderCode
	, productOrderCode
	, deliveryCode
	, shipmentPlanCode
	, returnRegCode
	, sendWarehouse
	, receiveWarehouse
	, deliveryCost
	, sendPlanDate
	, receivePlanDate
	, requestDate, adjDate
	, endDate
	, briefs
	, managerId, regDate, updateDate
)VALUES (
	CONCAT('stockAdjCode_',#{stockAdjCode})
	, #{mainBusinessCode}
	, ${stockReasonCode}
	, #{clientCode}
	, CONCAT('contractCode_', #{contractCode})
	, CONCAT('materialOrderCode_',#{materialOrderCode})
	, CONCAT('productOrderCode_',#{productOrderCode})
	, CONCAT('deliveryCode_',#{deliveryCode})
	, CONCAT('shipmentPlanCode_',#{shipmentPlanCode})
	, #{returnRegCode}
	, CONCAT('warehouseCode_',#{sendWarehouse})
	, CONCAT('warehouseCode_',#{receiveWarehouse})
	, ${deliveryCost}
	, #{sendPlanDate}
	, #{receivePlanDate}
	, #{requestDate}
	, #{adjDate}
	, #{endDate}
	, #{briefs}
	, #{managerId}
	, NOW()
	, NOW()
);

INSERT INTO k1_tb_stock_adjustment_detail
	(stockAdjDetailCode, stockAdjCode, itemCode
	, purchaseTsCode
	, salesTsCode
	, unitPrice
	, adjCount
	, afterCount
	, adjWeight
	, totalWeight
	, `status`
	, defectType
	, defectHandlingType
	, `comment`
)VALUES (
	CONCAT('stockAdjDetailCode_', #{stockAdjDetailCode})
	, CONCAT('stockAdjCode_', #{stockAdjCode})
	, CONCAT('itemCode_',#{itemCode})
	, CONCAT('purchase_ts_stCode_',#{purchaseTsCode})
	, CONCAT('salesTsCode_',#{salesTsCode})
	, #{unitPrice}
	, #{itemCount}
	, #{totalCount}
	, #{itemWeight}
	, #{totalWeight}
	, #{status}
	, #{defectType}
	, #{defectHandlingType}
	, #{comment}
);
