mapper.select:
SELECT RIGHT(a.stockAdjCode,3) AS stockAdjCode ,a.stockReasonCode ,r.reason AS stockReason ,a.clientCode ,c.businessName ,GROUP_CONCAT(DISTINCT ad.itemName SEPARATOR ', ') AS itemList ,a.totalPrice ,a.briefs ,a.requestDate ,a.adjDate ,a.endDate ,a.managerId ,u.userName AS managerName ,a.regDate ,LEFT(a.updateDate,10) AS updateDate FROM k1_tb_stock_adjustment AS a JOIN k1_tb_stock_reason AS r ON a.stockReasonCode = r.stockReasonCode JOIN k1_tb_user AS u ON a.managerId = u.userId JOIN (SELECT ad.stockAdjDetailCode ,ad.stockAdjCode ,s.inventoryCode ,s.itemName FROM k1_tb_stock_adjustment_detail AS ad JOIN k1_view_stock AS s ON ad.inventoryCode = s.inventoryCode ) AS ad ON a.stockAdjCode = ad.stockAdjCode LEFT JOIN k1_tb_client AS c ON a.clientCode= c.clientCode
a.mainBusinessCode = #{mainBusinessCode}
AND a.stockReasonCode = ${stockReasonCode}
GROUP BY a.stockAdjCode;
