mapper.select:
SELECT RIGHT(a.stockAdjCode,3) AS stockAdjCode ,a.stockReasonCode ,GROUP_CONCAT(ad.itemName) AS itemList ,a.totalPrice ,a.briefs ,a.requestDate ,a.adjDate ,a.endDate ,a.managerId ,u.userName AS managerName ,a.regDate ,a.updateDate FROM k1_tb_stock_adjustment AS a JOIN k1_tb_user AS u ON a.managerId = u.userId JOIN (SELECT ad.stockAdjDetailCode ,ad.stockAdjCode ,s.inventoryCode ,i.itemName FROM k1_tb_stock_adjustment_detail AS ad JOIN k1_tb_stock AS s ON ad.inventoryCode = s.inventoryCode JOIN k1_tb_item_info AS i ON s.itemCode = i.itemCode ) AS ad ON a.stockAdjCode = ad.stockAdjCode
a.mainBusinessCode = #{mainBusinessCode}
AND a.stockReasonCode = ${stockReasonCode}
GROUP BY a.stockAdjCode;
