mapper.select:
SELECT RIGHT(a.stockAdjCode,3) AS stockAdjCode ,a.reason AS stockReason ,a.clientCode ,a.businessName ,GROUP_CONCAT(DISTINCT ad.itemName SEPARATOR ', ') AS itemList ,a.totalPrice ,a.briefs ,a.requestDate ,a.adjDate ,a.endDate ,a.managerId ,a.managerName ,a.regDate ,LEFT(a.updateDate,10) AS updateDate FROM k1_view_stock_adjustment a JOIN (SELECT ad.stockAdjDetailCode ,ad.stockAdjCode ,i.itemName FROM k1_tb_stock_adjustment_detail ad JOIN k1_view_item_info i ON ad.itemCode = i.itemCode ) AS ad ON a.stockAdjCode = ad.stockAdjCode
a.mainBusinessCode = #{mainBusinessCode}
AND a.stockReasonCode = ${stockReasonCode}
GROUP BY a.stockAdjCode;
