mapper.update:
UPDATE 
	k1_tb_user
SET
	 userId = #{userId}
	,mainBusinessCode = #{mainBusinessCode}
	,userLevelCode = #{userLevelCode}
	,userName = #{userName}
	,userGender = #{userGender}
	,userAddr = #{userAddr}
	,userMoblie = #{userMoblie}
	,userEmail = #{userEmail}
	,userPosition = #{userPosition}
	,joinDate = #{joinDate}
WHERE 
	userId = #{userId};
