mapper.insert:
INSERT INTO k1_tb_material_order(
	materialOrderCode
	, mainBusinessCode
	, contractCode
	, clientCode
	, totalCount
	, totalPrice
	, orderDate
	, predictDate
	, `status`
	, briefs
	, managerId
	, regDate
	, updateDate)
VALUES (
	#{materialOrderCode}
	,#{mainBusinessCode}
	,#{contractCode}
	,#{clientCode} 
	,#{totalCount} 
	,#{totalPrice} 
	,#{orderDate} 
	,#{predictDate} 
	,#{status} 
	,#{briefs} 
	,#{managerId} 
	,curdate()
	,#{updateDate} 
	);

mapper.update:
update k1_view_material_order

	
		orderDate =#{orderDate}
	
	
		contractCode =#{clientBusinessName}
	
	
		clientCode =#{itemName}
	
	
		totalCount =#{totalCount}
	
	
		totalPrice =#{totalPrice}
	
	
		managerName =#{managerName}
	
	
		status =#{status}
	
	
		briefs =#{briefs}
	
	
		regDate =#{regDate}
	
	
		updateDate =#{updateDate}
	

WHERE 
	materialOrderCode=#{materialOrderCode};
